interface CardRutaProps {
  id: string;
  nombre: string;
  destino: string;
  imagen: string;
  precio: number;
  duracion: string;
  dificultad: "F√°cil" | "Moderado" | "Avanzado";
  rating: number;
  resenas: number;
}

