    e.preventDefault();
    setIsLoading(true);

    try {
      await login(email, password);
      toast({
        title: "¬°Bienvenido!",
        description: "Has iniciado sesi√≥n correctamente",
      });
      setLocation("/");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Credenciales inv√°lidas",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
