import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import CardRuta from "@/components/CardRuta";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";

interface Ruta {
  id: string;
  nombre: string;
  destino: string;
  dificultad: "F√°cil" | "Moderado" | "Avanzado";
  duracion: string;
  precio: number;
  imagenUrl: string;
  rating: string;
  resenas: number;
}

export default function Rutas() {
  const [destinoFilter, setDestinoFilter] = useState("todos");
  const [dificultadFilter, setDificultadFilter] = useState("todas");

  const { data: rutas, isLoading } = useQuery<Ruta[]>({
    queryKey: ["/api", "rutas", { destino: destinoFilter, dificultad: dificultadFilter }],
  });

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />

      <main className="flex-1">
        <div className="bg-primary/10 py-12 md:py-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h1 className="text-3xl md:text-4xl font-bold mb-4">
              Explora Nuestras Rutas
            </h1>
            <p className="text-lg text-muted-foreground">
              Encuentra la aventura perfecta en el Eje Cafetero
            </p>
          </div>
        </div>

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="mb-8 flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <label className="text-sm font-medium mb-2 block">Destino</label>
              <Select value={destinoFilter} onValueChange={setDestinoFilter}>
                <SelectTrigger data-testid="select-filter-destination">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="todos">Todos los destinos</SelectItem>
                  <SelectItem value="Salento, Quind√≠o">Salento</SelectItem>
                  <SelectItem value="Valle de Cocora">Valle de Cocora</SelectItem>
                  <SelectItem value="Filandia, Quind√≠o">Filandia</SelectItem>
                  <SelectItem value="Hacienda El Ocaso">Haciendas Cafeteras</SelectItem>
                  <SelectItem value="Finca Cafetera">Finca Cafetera</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex-1">
              <label className="text-sm font-medium mb-2 block">Dificultad</label>
              <Select value={dificultadFilter} onValueChange={setDificultadFilter}>
                <SelectTrigger data-testid="select-filter-difficulty">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="todas">Todas</SelectItem>
                  <SelectItem value="F√°cil">F√°cil</SelectItem>
                  <SelectItem value="Moderado">Moderado</SelectItem>
                  <SelectItem value="Avanzado">Avanzado</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="space-y-4">
                  <Skeleton className="aspect-video" />
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-4 w-1/2" />
                </div>
              ))}
            </div>
          ) : (
            <>
              {rutas && rutas.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {rutas.map((ruta) => (
                    <CardRuta
                      key={ruta.id}
                      id={ruta.id}
                      nombre={ruta.nombre}
                      destino={ruta.destino}
                      imagen={ruta.imagenUrl}
                      precio={ruta.precio}
                      duracion={ruta.duracion}
                      dificultad={ruta.dificultad}
                      rating={parseFloat(ruta.rating)}
                      resenas={ruta.resenas}
                    />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <p className="text-lg text-muted-foreground">
                    No se encontraron rutas con los filtros seleccionados
                  </p>
                </div>
              )}
            </>
          )}
        </div>
      </main>

      <Footer />
    </div>
  );
}

